import React from "react"
import Maverick from "./Maverick"
import Note from "./Note"

const App = () => {
  //eslint disable next line
  const [isEditing, setEditing] =
    Maverick.useState(false)
  const [inputValue, setInputValue] =
    Maverick.useState("")
  const [notes, setNotes] = Maverick.useState([])

  const updateNoteText = (event) => {
    setInputValue(event.target.value)
  }

  const handleKeyPress = (e) => {
    if (e.key === "Enter") {
      addTask()
    }
  }

  const addTask = () => {
    if (!inputValue.replace(/\s/, "").length) {
      return
    }

    const newNotesArray = [...notes, inputValue]
    setNotes(newNotesArray)
    setInputValue("")
  }

  const deleteTask = (index) => {
    const notesArray = [...notes]
    notesArray.splice(index, 1)
    setNotes(notesArray)
  }

  return (
    <div className="container">
      <div className="btn" onClick={addTask}>
        +
      </div>
      <input
        type="text"
        className="textInput"
        value={inputValue}
        onChange={updateNoteText}
        onKeyPress={handleKeyPress}
      />
      {notes.map((item, index) => (
        <Note
          item={item}
          onClick={() => deleteTask(index)}
          key={`task${index}`}
        />
      ))}
    </div>
  )
}

export default App
